def.body:
if (maps.has_key?(name)) && (WAD::Map.is_map?(name))
  return "Map"
end
if (pcsounds.has_key?(name)) && (WAD::PcSound.is_pcsound?(name))
  return "PcSound"
end
if (sounds.has_key?(name)) && (WAD::Sound.is_sound?(name))
  return "Sound"
end
if (music.has_key?(name)) && (WAD::Music.is_music?(name))
  return "Music"
end
if WAD::Genmidi.is_genmidi?(name)
  return "Genmidi"
end
if WAD::Dmxgus.is_dmxgus?(name)
  return "Dmxgus"
end
if WAD::Playpal.is_playpal?(name)
  return "Playpal"
end
if WAD::Colormap.is_colormap?(name)
  return "Colormap"
end
if WAD::EnDoom.is_endoom?(name)
  return "EnDoom"
end
if (texmaps.has_key?(name)) && (WAD::TextureX.is_texturex?(name))
  return "TexMap"
end
if WAD::Pnames.is_pnames?(name)
  return "Pnames"
end
if graphics.has_key?(name)
  return "Graphic"
end
if sprites.has_key?(name)
  return "Sprite"
end
if flats.has_key?(name)
  return "Flat"
end
if demos.has_key?(name)
  return "Demo"
end
return "Marker"

case prop.name
when "THINGS"
  @things_directory = prop
when "LINEDEFS"
  @linedefs_directory = prop
when "SIDEDEFS"
  @sidedefs_directory = prop
when "VERTEXES"
  @vertexes_directory = prop
when "SEGS"
  @segs_directory = prop
when "SSECTORS"
  @ssectors_directory = prop
when "NODES"
  @nodes_directory = prop
when "SECTORS"
  @sectors_directory = prop
when "REJECT"
  @reject_directory = prop
when "BLOCKMAP"
  @blockmap_directory = prop
end

File.open(file, "w+") do |file|
  return self.write(file, linedefs)
end

File.open(file, "w+") do |file|
  return self.write(file, nodes)
end

File.open(file, "w+") do |file|
  return self.write(file, sectors)
end

File.open(file, "w+") do |file|
  return self.write(file, segs)
end

File.open(file, "w+") do |file|
  return self.write(file, sidedefs)
end

File.open(file, "w+") do |file|
  return self.write(file, ssectors)
end

File.open(file, "w+") do |file|
  return self.write(file, things)
end

File.open(file, "w+") do |file|
  return self.write(file, vertexes)
end
